'''
Given a string S and a string T, find the minimum window in S which will contain all the characters in T in complexity O(n).

For example,
S = "ADOBECODEBANC"
T = "ABC"
Minimum window is "BANC".

Note:
If there is no such window in S that covers all characters in T, return the empty string "".

If there are multiple such windows, you are guaranteed that there will always be only one unique minimum window in S.

'''

class Solution(object):
    #Time limit exceeded....
    def MyminWindow(self, s, t):
        """
        :type s: str
        :type t: str
        :rtype: str
        """
        
        if t is None:
            return ""
        #a map to save the position index for each char in t
        index = {}
        #a list to save the sequence of t
        seq = []
        head = tail = w_head = w_tail = -1
        tt = t
        for i in xrange(len(s)):
            if s[i] in t:
                if index.has_key(s[i]):
                    index[s[i]].append(i)
                else:
                    index[s[i]] = [i]
                seq.append(s[i])
                #save the first head
                if head is -1:
                    head = i
                #help to find the first tail
                tt = tt.replace(s[i], '', 1)
            #save the firt window's end position    
            if tail is -1 and tt == '':
                tail = i
        
        #the s does not contains all char in t
        if tail is -1:
            return ""
        
        #current window size
        window_size = 999999999
        head_queue = []
        head_index = {}
        for ss in seq:
#            if len(index[s])==0:
#                break 
            pos = index[ss].pop(0)
            head_index[ss] = pos
            
            #to save the next head
            if head_queue.count(ss) is t.count(ss):
                head_queue.remove(ss)
                head = head_index[head_queue[0]] if len(head_queue) > 0 else pos
            head_queue.append(ss)
            
            if pos > tail:
                tail = pos
                  
            if tail - head < window_size:
                w_head = head
                w_tail = tail
                window_size = tail - head
        return s[w_head: w_tail + 1]
        
        
    def minWindow(self, s, t):
        if t is None:
            return ""
        
        index = {}
        for i in xrange(len(t)):
            index[t[i]] = index[t[i]] + 1 if index.has_key(t[i]) else 1
        
        count = 0
        head = 0
        min_size = len(s) + 1
        left = 0
        for i in xrange(len(s)):
            c = s[i]
            if index.has_key(c):
                index[c] = index[c] - 1
                if index[c]>= 0:
                    count+=1
                                
                while count==len(t):
                    if i - left + 1 < min_size:
                        head = left
                        min_size = i - left + 1
                        
                    if index.has_key(s[left]):
                        index[s[left]] = index[s[left]] + 1
                        if index[s[left]]> 0:
                            count-=1  
                    left+=1
        if min_size > len(s):
            return ""
        return s[head: head+min_size]
        
         
